import React, { useState, useEffect } from 'react';
import SiteAnalysisPanel from './components/SiteAnalysisPanel';
import SensorDashboard from './components/SensorDashboard';
import TerrainMap from './components/TerrainMap';
import SiteComparison from './components/SiteComparison';
import { useWebSocket } from './hooks/useWebSocket';

const App: React.FC = () => {
  const { data, connected } = useWebSocket('ws://localhost:8765');

  const [sites, setSites] = useState<any[]>([]);
  const [robotPosition, setRobotPosition] = useState({ x: 0, y: 0 });
  const [habitatData, setHabitatData] = useState<any>(null);

  useEffect(() => {
    if (data.sites) {
      setSites(data.sites.sites || []);
      if (data.sites.robot_position) {
        setRobotPosition(data.sites.robot_position);
      }
    }
    if (data.habitat) {
      setHabitatData(data.habitat);
    }
  }, [data]);

  return (
    <div className="flex h-screen bg-gray-900 text-white">
      {/* Left: Webots Simulation (placeholder for iframe) */}
      <div className="w-1/2 p-4 bg-gray-800 border-r border-gray-700">
        <div className="h-full flex flex-col">
          <h2 className="text-xl font-bold mb-4 text-blue-400">Webots Simulation</h2>
          <div className="flex-1 bg-black rounded-lg flex items-center justify-center">
            <div className="text-center">
              <p className="text-gray-400">Webots Simulation View</p>
              <p className="text-sm text-gray-500 mt-2">Robot Position: ({robotPosition.x.toFixed(2)}, {robotPosition.y.toFixed(2)})</p>
              <p className={`mt-2 text-sm ${connected ? 'text-green-400' : 'text-red-400'}`}>
                WebSocket: {connected ? 'Connected' : 'Disconnected'}
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Right: LunaBot Dashboard */}
      <div className="w-1/2 p-4 bg-gray-900 overflow-y-auto">
        <h1 className="text-2xl font-bold mb-6 text-center text-blue-400">LunaBot Habitat Site Selection Dashboard</h1>

        <div className="grid grid-cols-1 gap-4">
          {/* Site Analysis Panel */}
          <div className="bg-gray-800 rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-2 text-green-400">Top Habitat Sites</h3>
            <SiteAnalysisPanel sites={sites} />
          </div>

          {/* Sensor Dashboard */}
          <div className="bg-gray-800 rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-2 text-yellow-400">Environmental Sensors</h3>
            <SensorDashboard habitatData={habitatData} />
          </div>

          {/* Site Comparison */}
          <div className="bg-gray-800 rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-2 text-purple-400">Site Comparison</h3>
            <SiteComparison sites={sites} />
          </div>

          {/* 3D Terrain Visualization */}
          <div className="bg-gray-800 rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-2 text-red-400">3D Terrain View</h3>
            <TerrainMap sites={sites} robotPosition={robotPosition} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
