import React from 'react';
import { Radar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, ResponsiveContainer, Legend } from 'recharts';

interface Site {
  position: { x: number; y: number };
  scores: {
    safety: number;
    buildability: number;
    resources: number;
    expandability: number;
  };
  total_score: number;
}

interface SiteComparisonProps {
  sites: Site[];
}

const SiteComparison: React.FC<SiteComparisonProps> = ({ sites }) => {
  // Prepare data for radar chart (top 3 sites)
  const topSites = sites.slice(0, 3);

  // Format data for radar chart
  const radarData = [
    { subject: 'Safety', ...topSites.reduce((acc, site, idx) => ({...acc, [`site${idx+1}`]: site.scores.safety}), {}) },
    { subject: 'Buildability', ...topSites.reduce((acc, site, idx) => ({...acc, [`site${idx+1}`]: site.scores.buildability}), {}) },
    { subject: 'Resources', ...topSites.reduce((acc, site, idx) => ({...acc, [`site${idx+1}`]: site.scores.resources}), {}) },
    { subject: 'Expandability', ...topSites.reduce((acc, site, idx) => ({...acc, [`site${idx+1}`]: site.scores.expandability}), {}) },
    { subject: 'Total', ...topSites.reduce((acc, site, idx) => ({...acc, [`site${idx+1}`]: site.total_score}), {}) },
  ];

  // Define colors for each site
  const siteColors = [
    { stroke: '#8b5cf6', fill: '#8b5cf6', name: 'Site 1' },
    { stroke: '#3b82f6', fill: '#3b82f6', name: 'Site 2' },
    { stroke: '#22c55e', fill: '#22c55e', name: 'Site 3' }
  ];

  return (
    <div>
      {sites.length > 0 ? (
        <div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-5 mb-6">
            {topSites.map((site, index) => (
              <div key={index} className="bg-gradient-to-br from-gray-800 to-gray-900 rounded-xl p-5 border border-gray-700 shadow-lg">
                <div className="flex justify-between items-start mb-3">
                  <h4 className="font-bold text-lg">
                    <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-cyan-400">
                      SITE {index + 1}
                    </span>
                  </h4>
                  <div className={`w-3 h-3 rounded-full ${index === 0 ? 'bg-purple-500' : index === 1 ? 'bg-blue-500' : 'bg-green-500'} animate-pulse`}></div>
                </div>

                <div className="space-y-3">
                  <div className="flex justify-between border-b border-gray-700 pb-2">
                    <span className="text-gray-400">Position:</span>
                    <span className="font-mono text-cyan-400">({site.position.x.toFixed(2)}, {site.position.y.toFixed(2)})</span>
                  </div>

                  <div className="flex justify-between">
                    <span className="text-red-400">Safety:</span>
                    <span className="font-bold">{site.scores.safety.toFixed(1)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-yellow-400">Buildability:</span>
                    <span className="font-bold">{site.scores.buildability.toFixed(1)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-blue-400">Resources:</span>
                    <span className="font-bold">{site.scores.resources.toFixed(1)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-green-400">Expandability:</span>
                    <span className="font-bold">{site.scores.expandability.toFixed(1)}</span>
                  </div>
                  <div className="flex justify-between font-extrabold border-t border-gray-700 pt-2 mt-2 text-lg">
                    <span className={`text-transparent bg-clip-text bg-gradient-to-r ${index === 0 ? 'from-purple-400 to-purple-600' : index === 1 ? 'from-blue-400 to-blue-600' : 'from-green-400 to-green-600'}`}>
                      Total Score:
                    </span>
                    <span className={`text-transparent bg-clip-text bg-gradient-to-r ${index === 0 ? 'from-purple-400 to-purple-600' : index === 1 ? 'from-blue-400 to-blue-600' : 'from-green-400 to-green-600'}`}>
                      {site.total_score.toFixed(1)}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="bg-gray-800/50 p-4 rounded-xl border border-gray-700">
            <h5 className="text-sm font-semibold mb-3 text-cyan-400">Site Comparison Radar</h5>
            <div className="h-72">
              <ResponsiveContainer width="100%" height="100%">
                <RadarChart cx="50%" cy="50%" outerRadius="70%" data={radarData}>
                  <defs>
                    {siteColors.map((color, index) => (
                      <linearGradient key={`color-${index}`} id={`colorGradient-${index}`} x1="0" y1="0" x2="0" y2="1">
                        <stop offset="0%" stopColor={color.stroke} stopOpacity={0.4} />
                        <stop offset="100%" stopColor={color.stroke} stopOpacity={0.1} />
                      </linearGradient>
                    ))}
                  </defs>
                  <PolarGrid stroke="#374151" />
                  <PolarAngleAxis 
                    dataKey="subject" 
                    tick={{ fill: '#9CA3AF', fontSize: 12 }}
                    tickLine={false}
                    axisLine={false}
                  />
                  <PolarRadiusAxis 
                    angle={90} 
                    domain={[0, 100]} 
                    tick={{ fill: '#9CA3AF', fontSize: 10 }}
                    tickCount={6}
                  />
                  {topSites.map((_, index) => (
                    <Radar
                      key={index}
                      name={`Site ${index + 1}`}
                      dataKey={`site${index + 1}`}
                      stroke={siteColors[index].stroke}
                      fill={`url(#colorGradient-${index})`}
                      fillOpacity={0.6}
                      strokeWidth={2}
                    />
                  ))}
                  <Legend 
                    wrapperStyle={{ 
                      paddingTop: '15px',
                      color: '#9CA3AF',
                      fontSize: '12px'
                    }} 
                  />
                </RadarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      ) : (
        <div className="text-center py-12">
          <div className="inline-block p-4 rounded-full bg-gray-800 mb-4">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-12 w-12 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
          </div>
          <h3 className="text-lg font-semibold text-gray-400 mb-2">No Sites to Compare</h3>
          <p className="text-gray-500 max-w-md mx-auto">
            The LunaBot has not yet identified suitable habitat sites for comparison. 
            Please wait for the robot to complete its analysis.
          </p>
        </div>
      )}
    </div>
  );
};

export default SiteComparison;
